import { useEffect } from 'react'
import Button from '../Button'
import './CartTotal.scss'

export default function CartTotal({total , setTotal , found}) {
    useEffect(()=>{
        if(found){
            const sum = found.map ((item) => item[0].price * item[0].count)
            const reducer = (acc, cur) => acc + cur;
            if(sum.length === 0){
                setTotal(0);
                return
            }
            const itemTotal = sum.reduce(reducer);
            setTotal(itemTotal)
        }else{
            setTotal(0)
        } 
    },[cart,total,found,setTotal])
  return (
    <div className='total'>
        <div className='total_box'>
            <div className='total_box_left'>
                <p>삼품금액</p>
                <p>배송비</p>
                <h5 >총상품금액</h5>
            </div>
            <div className='total_box_right'>
                <p>0</p>
                <p>5$</p>
                <h5>0</h5>
            </div>
        </div>
        <div className='totalBtn'>
            <Button size="large"  color="gray">선택구매</Button>
            <Button size="large"  color="gray">구매</Button>
        </div>
    </div>
  )
}
