import {Route, Routes} from 'react-router-dom'
import {createGlobalStyle} from "styled-components";
import reset from "styled-reset"
import Cart from './pages/Cart';
import Home from './pages/Home';
import Shop from './pages/Shop';
import Header from './components/header/Header';
import ShopDtail from './pages/ShopDetail';
import Footer from './components/footer/Footer'
import { useState } from 'react';





function App() {
  const GlobalStyles = createGlobalStyle`
  @import url('https://fonts.googleapis.com/css2?family=Josefin+Sans&family=Noto+Sans+KR:wght@100&display=swap');
    /* 
    html{
      padding: 0;
      margin: 0;
      box-sizing: border-box;

    }
    body{
      font-family: 'Josefin Sans', sans-serif;


    }

    a{
      text-decoration: none;
    }
    .container{
      width: 1056px;
      margin: 0 auto;

    } */
    @media screen and (max-width:1057px) {
      .container{
      width: 100%;


    }
  }
  `;

  const [cart, setCart] = useState([])
  const [checkList , setCheckList] = useState([])

  return (
    <>
      <GlobalStyles/>
      <Header cart={cart}/>
      <Routes>
        <Route path='/' element={<Home/>}/>
        <Route path='/shop' element={<Shop/>}/>
        <Route path='/shop/:category' element={<Shop/>}/>
        <Route path='/shop/detail/:id' element={<ShopDtail cart={cart} setCart={setCart}/>}/>
        <Route path='/cart' element={<Cart cart={cart} setCart={setCart} checkList={checkList} setCheckList={setCheckList} /> }/>
      </Routes>
      {/* <Footer/> */}
    </>

  );
}

export default App;
