import React, { useState } from 'react'
import './CartItem.scss'


export default function CartItem({cart, setCart}) {
    console.log(cart)
    const [itemNum, setItemNum] = useState(1)

     const handlCount = (count, id)=>{
        const found = cart.filter((el) => el.id ===id)[0];
        const idx = cart.indexOf(found);
        const cartItem ={
            id: cart.id,
            image:cart.image,
            title:cart.title,
            price:cart.price,
            count:count
        }
        setCart([...cart.slice(0,idx), cartItem, ...cart.slice(idx+1)])
    } 
    const handleCart = (count) =>{
        const cartItem = {
            id: cart.id,
            image:cart.image,
            title:cart.title,
            price:cart.price,
            count:count
        }
        const found = cart.find((el) => el.id === cartItem.id)
        if(found)handlCount(cartItem.id, found.count + count)
        else{
            setCart
        }
    }
    
    
    

  return (
    <li className='cart_item_list'>
        <input type="checkbox" />
        <div className='cart_img'>
            <img src={cart.image} alt="" />
        </div>
        <p className='cart_item_name'>{cart.title}</p>
        <p className='cart_item_price'>{cart.price * cart.count}</p>
        <div className='cart_item_count'>
            <button className='item_count_btn'>-</button>
            <span>{cart.count}</span>
            <button className='item_count_btn'>+</button>
        </div>
    </li>
  )
}
